import React, { useState } from 'react'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import moment from "axios";




const Write = () => {
  const state = useLocation().state;
  const [value, setValue] = useState('');
  const [title, setTitle] = useState('');
  const [img, setImg] = useState('');
  const [file, setFile] = useState(null);
  const [cat, setCat] = useState(state?.cat || "");

  const navigate = useNavigate()

  const upload = async () => {
    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await axios.post("/upload", formData);
      return res.data;
    } catch (err) {
      console.log(err);
    }
  };

  const handleClick = async (e) => {
    e.preventDefault();
    const imgUrl = await upload();

    try {
      state 
      ? await axios.put(`/posts/${state.id}`, {
            title,
            desc: value,
            cat,
            img: file ? imgUrl : "",
          })
        : await axios.post(`/posts/`, {
            title,
            desc: value,
            cat,
            img: file ? imgUrl : "",
            date: moment(Date.now()).format("YYYY-MM-DD HH:mm:ss"),
          });
          navigate("/")
    } catch (err) {
      console.log(err);
    }
  };



  return (
    <div className="add">
      <div className="content">
        <input type="text" placeholder='Title' onChange={e=>setTitle(e.target.value)}/>
        <div className="editorContainer">
        <ReactQuill className="editor" theme="snow" value={value} onChange={setValue} />
        </div>
      </div>
      <div className="menu">
        <div className="item">
          <h1>Publish</h1>
          <span>
            <b>Status:</b>Draft
          </span>
          <span>
            <b>Visibility:</b>Public
          </span>
          <input style={{ display: "none" }} type="file"
            id="file"
            name=""
            onChange={(e) => setFile(e.target.files[0])}
          />
          <label htmlFor="file">Upload Image</label>
        <div className="buttons">
          <button>Save as a draft</button>
          <button onClick={handleClick}> Publish</button>
        </div>
        </div>
        <div className="item">
          <h1>Category</h1>
          <div className="cat">
          <input type="radio" name="cat" value="art" id="art" onChange={e=>setCat(e.target.value)}/>
          <label htmlFor="art">Art</label>
          </div>
          <div className="cat">
          <input type="radio" name="cat" value="science" id="science" onChange={e=>setCat(e.target.value)}/>
          <label htmlFor="science">science</label>
          </div>
          <div className="cat">
          <input type="radio" name="cat" value="technology" id="technology" onChange={e=>setCat(e.target.value)}/>
          <label htmlFor="technology">Technology</label>
          </div>
          <div className="cat">
          <input type="radio" name="cat" value="cinema" id="cinema" onChange={e=>setCat(e.target.value)}/>
          <label htmlFor="cinema">cinema</label>
          </div>
          <div className="cat">
          <input type="radio" name="cat" value="design" id="design" onChange={e=>setCat(e.target.value)}/>
          <label htmlFor="design">Design</label>
          </div>
          <div className="cat">
          <input type="radio" name="cat" value="food" id="food" onChange={e=>setCat(e.target.value)}/>
          <label htmlFor="food">Food</label>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Write
